def hex2mx(hex_str, width, height):
    assert width * height == len(hex_str) * 4

    #xxx bs = bytes.fromhex(hex_str)
    u = int(hex_str, base=16)
    bs = bin(u)[2:]
    bs = bytes(0 if b == '0' else 1 for b in bs)
    bs = bytes((width * height) - len(bs)) + bs
    assert len(bs) == width * height
    
    mx = []
    for i in range(0, len(bs), width):
        mx.append(bs[i : i+width])

    return mx

    
def read_as_ord2hex(font_file):
    ord2hex = [None] * (2**16)
    
    with open(font_file) as txt:
        for line in txt:
            unicode16 = line[:4]
            c_m = line[4]
            hex_str = line[5:-1]
            c_e = line[-1]
            assert c_m == ':'
            assert c_e == '\n'
            assert len(hex_str) % 4 == 0

            u = int(unicode16, base=16)
            ord2hex[u] = hex_str

    return ord2hex

def read(font_file):
    ord2hex = read_as_ord2hex(font_file)
    uint16_to_mx = [hex2mx(hex_str, len(hex_str)//4, 16) if hex_str else None for hex_str in ord2hex]

    return uint16_to_mx


