def mx_size(mx):
    if not len(mx):
        return ()

    width = len(mx)
    height = len(mx[0])
    return width, height

def xy2color(mx, x, y):
    size = mx_size(mx)
    if not size:
        return 0

    width, height = mx_size(mx)
    if not 0 <= x < width: return 0
    if not 0 <= y < height: return 0

    return mx[x][y]

def rect2uint(mx, xy0, size):
    w, h = size
    x, y = xy0

    bs = []
    for j in range(y, y+h):
        for i in range(x, x+w):
            bs.append('1' if xy2color(mx, i, j) else '0')

    bs = ''.join(bs)

    u = int(bs, base=2)
    return u


def diff(u, v):
    i = u ^ v
    d = bin(i)[2:].count('1')
    return d


def near_chr(u, chrs, uints):
    ls = [diff(u, v) for v in uints]
    i = ls.index(min(ls))
    return chrs[i]
        
def mx2chrmx(mx, chrs, uints, size):
    if not mx_size(mx):
        return ()

    width, height = mx_size(mx)
    dx, dy = size
    chrmx = []
    #print(width, height, dx, dy)
    for y in range(0, height, dy):
        row = []
        for x in range(0, width, dx):
            #print(x,y)
            u = rect2uint(mx, (x,y), size)
            c = near_chr(u, chrs, uints)
            row.append(c)
        row = ''.join(row)
        chrmx.append(row)

    return chrmx
    
